/**
 *
 */
function SmokeDetector() {
    /**
     *
     */
    SmokeDetector.prototype.start = function () {
        var deferred = q.defer();

        this.state = {consumption: 0};

        if (this.isSimulated()) {
            setInterval(function () {
                this.state.batteryLevel = 100 - 20 * Math.floor((Math.random() * 6));
                this.state.particleConcentration = 0.4 + 0.1 * Math.floor((Math.random() * 3));

                this.publishStateChange();

                var threshold = this.configuration && this.configuration.particleConcentrationThreshold ? this.configuration.particleConcentrationThreshold : 0.5;

                if (threshold) {
                    this.publishEvent("alarm");
                }
            }.bind(this), 10000);
            deferred.resolve();
        } else {
            deferred.resolve();
        }

        return deferred.promise;
    };

    /**
     *
     */
    SmokeDetector.prototype.setState = function (state) {
        this.state = state;

        this.publishStateChange();
    };

    /**
     *
     */
    SmokeDetector.prototype.getState = function () {
        return this.state;
    };
}
