function ActorConfigurationDialog() {
    /**
     *
     */
    ActorConfigurationDialog.prototype.initialize = function (console, device, actor) {
        this.id = "actorConfigurationDialog";
        this.console = console;

        this.device = device;

        if (actor) {
            this.actor = actor;
        } else {
            this.actor = {configuration: {}};
        }

        return this;
    };

    /**
     *
     */
    ActorConfigurationDialog.prototype.actorTypeChanged = function () {
        // TODO Into Actor Module

        var actorType = this.console.actorType(this.device, this.actor.type);

        this.actor.id = Utils.getNextDefaultId(this.device.actors, actorType.plugin, 0);
        this.actor.label = Utils.getNextDefaultLabel(this.device.actors, actorType.label, 0);

        for (var n in actorType.configuration) {
            if (actorType.configuration[n].defaultValue) {
                this.actor.configuration[actorType.configuration[n].id] = actorType.configuration[n].defaultValue;
            }
        }
    };

    /**
     *
     */
    ActorConfigurationDialog.prototype.show = function () {
        var deferred = $.Deferred();

        deferred.resolve();

        return deferred.promise();
    };

    /**
     *
     */
    ActorConfigurationDialog.prototype.leave = function () {
    };
}
