function DeviceConfigurationDialog() {
    /**
     *
     */
    DeviceConfigurationDialog.prototype.initialize = function (console, device) {
        this.id = "deviceConfigurationDialog";
        this.console = console;

        if (device) {
            this.device = device;
            this.mode = "update";
        } else {
            this.device = {configuration: {}};

            this.mode = "create";
        }


        return this;
    };

    /**
     *
     */
    DeviceConfigurationDialog.prototype.deviceTypeChanged = function () {
        // TODO Into Device Module

        var deviceType = this.console.deviceTypes[this.device.plugin];

        this.device.id = Utils.getNextDefaultId(this.console.node.devices, deviceType.plugin, 0);
        this.device.label = Utils.getNextDefaultLabel(this.console.node.devices, deviceType.label, 0);

        for (var n in deviceType.configuration) {
            if (deviceType.configuration[n].defaultValue) {
                this.device.configuration[deviceType.configuration[n].id] = deviceType.configuration[n].defaultValue;
            }
        }
    };

    /**
     *
     */
    DeviceConfigurationDialog.prototype.show = function () {
        var deferred = $.Deferred();

        deferred.resolve();

        return deferred.promise();
    };

    /**
     *
     */
    DeviceConfigurationDialog.prototype.leave = function () {
    };
}
