function DeviceConfigurationPage() {
    /**
     *
     */
    DeviceConfigurationPage.prototype.initialize = function (console, device) {
        this.id = "deviceConfiguration";
        this.console = console;
        this.deviceId = device.id;

        return this;
    };

    /**
     *
     */
    DeviceConfigurationPage.prototype.show = function () {
        var deferred = $.Deferred();

        // Node may have been refreshed

        this.device = this.console.node.getDevice(this.deviceId);

        deferred.resolve();

        return deferred.promise();
    };

    /**
     *
     */
    DeviceConfigurationPage.prototype.leave = function () {
    };
}
