function DevicesConfigurationPage() {
    /**
     *
     */
    DevicesConfigurationPage.prototype.initialize = function (console, node) {
        this.id = "devicesConfiguration";
        this.console = console;
        this.node = node;

        return this;
    };

    /**
     *
     */
    DevicesConfigurationPage.prototype.show = function () {
        var deferred = $.Deferred();

        try {
            this.initializeSpeech();

            deferred.resolve();
        }
        catch (error) {
            console.trace(error);

            deferred.reject(error)
        }

        return deferred.promise();
    };

    /**
     *
     */
    DevicesConfigurationPage.prototype.initializeSpeech = function () {
        var context = {productions: []};

        context.productions.push({
            name: "help",
            tokens: "help",
            synophones: "held",
            action: function () {
                this.console.speech.utter("You have selected node " + this.node.label);
            }.bind(this)
        });
        context.productions.push({
            name: "Context",
            tokens: "context",
            action: function () {
                this.console.speech.utter("You have selected node " + this.node.label);
            }.bind(this)
        });

        for (var n in this.node.groups) {
            context.productions.push({
                name: "Group Navigation",
                tokens: this.node.groups[n].label,
                action: function () {
                    this.console.speech.utter("switching context to group " + this.node.groups[n].label);
                    this.console.pushGroupPage(this.node.groups[n]);
                }.bind(this)
            });
        }

        this.console.speech.setContext(context);
    };

    /**
     *
     */
    DevicesConfigurationPage.prototype.leave = function () {
    };
}
