function DevicesConfigurationPage() {
    /**
     *
     */
    DevicesConfigurationPage.prototype.initialize = function (console) {
        this.id = "devicesConfiguration";
        this.console = console;

        return this;
    };

    /**
     *
     */
    DevicesConfigurationPage.prototype.show = function () {
        var deferred = $.Deferred();

        try {
            this.initializeSpeech();

            deferred.resolve();
        }
        catch (error) {
            console.error(error);

            deferred.reject(error)
        }

        return deferred.promise();
    };

    /**
     *
     */
    DevicesConfigurationPage.prototype.initializeSpeech = function () {
    };

    /**
     *
     */
    DevicesConfigurationPage.prototype.leave = function () {
    };
}
