function LoginPage() {
    /**
     *
     */
    LoginPage.prototype.initialize = function (console) {
        this.console = console;
        this.id = "login";

        return this;
    };

    /**
     *
     */
    LoginPage.prototype.show = function () {
        var deferred = $.Deferred();

        if (window.localStorage["thing-it.account"] != undefined && window.localStorage["thing-it.password"] != undefined &&
            window.localStorage["thing-it.account"] != "" && window.localStorage["thing-it.password"] != "") {
            this.account = window.localStorage["thing-it.account"];
            this.password = window.localStorage["thing-it.password"];

            this.rememberMe = true;
        }
        else {
            this.rememberMe = false;
        }

        deferred.resolve();

        return deferred.promise();
    };

    /**
     *
     */
    LoginPage.prototype.leave = function () {
    };

    /**
     *
     */
    LoginPage.prototype.login = function () {
        if (this.rememberMe) {
            window.localStorage["thing-it.account"] = this.account;
            window.localStorage["thing-it.password"] = this.password;
        } else {
            window.localStorage["thing-it.account"] = "";
            window.localStorage["thing-it.password"] = "";
        }

        this.console.login({account: this.account, password: this.password});
    };
}
